mapper.insert:
MERGE INTO CAT_BOARD BOARD1
     USING (SELECT #{BOARD_SEQ} AS BOARD_SEQ FROM DUAL) BOARD2
        ON (BOARD1.BOARD_SEQ = BOARD2.BOARD_SEQ)
 WHEN MATCHED THEN
    UPDATE SET
		NAME = #{NAME} 	
		, TELEPHONE = #{TELEPHONE} 	
		, ORDER_NUMBER = #{ORDER_NUMBER} 	
		, PARENT_ORGANIZATION_SEQ = #{PARENT_ORGANIZATION_SEQ} 	
 WHEN NOT MATCHED THEN
    INSERT (
		ORGANIZATION_SEQ	
		, NAME	
		, TELEPHONE	
		, ORDER_NUMBER	
		, PARENT_ORGANIZATION_SEQ	
	)
	values(
		#{ORGANIZATION_SEQ}	
		, #{NAME}	
		, #{TELEPHONE}	
		, #{ORDER_NUMBER}	
		, #{PARENT_ORGANIZATION_SEQ}	
	)
